    {key: "trending", text: "TRENDING", value: "trending" },
    {key: "new", text: "NEW", value: "new" }
]

class DropdownExampleHeader extends Component {

    constructor(props) {
      super(props)
    
      this.state = {
         
      };
    };
    

    changeMethod = (event, data) => {
        this.setState({
           filterValue: data.value
        })
        console.log(data)
      }

  render() {
    console.log("on change follower",this.state.filterValue)
    return (
      <div> 
          <Dropdown
            placeholder='All'
            text='Filter'
            icon='filter'
            floating
            labeled
            button
            className='icon'
            selection
            name="category search"
            options={optionType}
            onChange={this.changeMethod}
        />
            {/* <Dropdown.Menu>
            <Dropdown.Header icon='tags' content='Filter by tag' />
            <Dropdown.Item>Important</Dropdown.Item>
            <Dropdown.Item>Announcement</Dropdown.Item>
            <Dropdown.Item>Discussion</Dropdown.Item>
            </Dropdown.Menu>
        </Dropdown> */}
      </div>
    );
  }
}

export default DropdownExampleHeader;
