import React, { Component } from 'react';
import Dropdown from '../dropdown/DropDown'


class ExhibitionDirectory extends Component {
  render() {
    return (
        <div>
        <div style={headStyle}>
        <h3 style={textStyle}>Exhibition Directory</h3>
      </div>
      <Dropdown title='Search by Category' items={items}  />
      </div>
    );
  }
}

export default ExhibitionDirectory;

const headStyle = {
    width: '100%',
    height: 70,
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: "#eee"
  }
  const textStyle = {
    flex: 1, 
    //paddingLeft: 5, 
    paddingTop: 5, 
    textAlign: 'center' ,
    fontSize: 30
  }

  const items = [
    {
      id: 1,
      value: 'Company 1'
    },
    {
      id: 2,
      value: 'Company 2'
    },
    {
      id: 3,
      value: 'Company 3'
    },
  ]
