import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import BottomNavigation from '@material-ui/core/BottomNavigation';
import BottomNavigationAction from '@material-ui/core/BottomNavigationAction';
import RestoreIcon from '@material-ui/icons/Restore';
import FavoriteIcon from '@material-ui/icons/Favorite';
import LocationOnIcon from '@material-ui/icons/LocationOn';
import Modal from '@material-ui/core/Modal';
import InfoSearchBox from '../InfoSearchBox'
import './bottombar.css'

const useStyles = makeStyles({
  root: {
    width: 1000,
    height: 80,
    position: 'absolute',
    bottom: 50,
    left: 800,
    backgroundColor: 'transparent'
  },
  paper: {
    position: 'absolute',
    width: 400,
    //backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    //boxShadow: theme.shadows[5],
    //padding: theme.spacing(2, 4, 3),
  },
});

const headStyle = {
  width: '100%',
  height: 90,
  backgroundColor: 'gray',
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center'
}
const textStyle = {
  flex: 1, 
  //paddingLeft: 5, 
  paddingTop: 5, 
  textAlign: 'center' ,
  fontSize: 30
}

export default function SimpleBottomNavigation() {
  const classes = useStyles();
  const [value, setValue] = React.useState(0);
  const [open, setOpen] = React.useState(true);

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

 

  return (
    <div>
      <BottomNavigation
        value={value}
        onChange={(event, newValue) => {
          setValue(newValue);
        }}
        showLabels
        className={classes.root}
      >
        <BottomNavigationAction onClick={handleOpen} label="Recents" icon={<RestoreIcon />} style={{ height: 70, width: 60, backgroundColor: 'black', color: 'white' }} />
        <BottomNavigationAction label="Favorites" icon={<FavoriteIcon />} style={{ height: 70, width: 60, backgroundColor: 'black', color: 'white' }} />
        <BottomNavigationAction label="Nearby" icon={<LocationOnIcon />} style={{ height: 70, width: 60, backgroundColor: 'black', color: 'white' }} />
      </BottomNavigation>
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="simple-modal-title"
        aria-describedby="simple-modal-description"
      >
        <div className='modal-div'>

          <div style={headStyle}>
            <h4 style={textStyle}>Hall 01 Directory</h4>
          </div>
         
          <InfoSearchBox />



        </div>
      </Modal>
    </div>
  );
}
