import React from 'react';
import { BrowserRouter, Switch, Route } from 'react-router-dom'
import SignIn from './components/auth/SignIn'
import SignUp from './components/auth/SignUp'
import Dashboard from './components/dashboard/Dashboard';
import StallDisplay from './components/stalldisplay/StallDisplay'
import StoreList from './components/storelist/StoreList'
import { connect } from "react-redux";
import Loading from "./components/Loaders/GlobalLoading";

class App extends React.Component {
  render() {
    console.log(this.props);
    let app = <Loading />
    if(
      this.props.auth.isAuthenticated &&
       this.props.auth.isLoading 
      // this.props.auth.appLoaded
    ){
      console.log('inside app render');
      app = (
        <BrowserRouter basename="/event_portal">
      <div className="App">
       {/* <Navbar /> */}
        <Switch>
        <Route path='/' exact component={SignIn}></Route>
          <Route path='/dashboard' exact component={Dashboard}></Route>
          <Route path='/login' component={SignIn}></Route>
          <Route path='/register' component={SignUp}></Route>
          <Route path='/stalldisplay' component={StallDisplay}></Route>
          <Route path='/storelist' component={StoreList}></Route>
        </Switch>
      </div>
      </BrowserRouter>
      );

    }
    return <div className="App">{app}</div>;
    // return(
      // <BrowserRouter basename="/event_portal">
      // <div className="App">
      //  {/* <Navbar /> */}
      //   <Switch>
      //   <Route path='/' exact component={SignIn}></Route>
      //     <Route path='/dashboard' exact component={Dashboard}></Route>
      //     <Route path='/login' component={SignIn}></Route>
      //     <Route path='/register' component={SignUp}></Route>
      //     <Route path='/stalldisplay' component={StallDisplay}></Route>
      //     <Route path='/storelist' component={StoreList}></Route>
      //   </Switch>
      // </div>
      // </BrowserRouter>
    // );
  }
}

const mapDispactchToProps = (dispatch) => ({
 // loadUser: () => dispatch(GetUser()),
});

const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps, mapDispactchToProps)(App);
