import React, { Component } from 'react';
// import Notification from './Notification'
// import ProjectList from '../../project/ProjectList';
import background from '../../assets/images/lobbey.jpg'
import AppBar from '../../components/AppBar'
import BottomBar from '../../components/bottombar/BottomBar'
import Modal from '@material-ui/core/Modal';
import './dashboard.css';
// import CustomListItem from '../ListItem'
import ChatBox from '../../components/chatbox/ChatBox'
import Dropdown from '../../components/dropdown/DropDown'
import ExhibitionDirectory from '../../components/exhibitionsearch/ExhibitionSearch'


const imageStyle = {
  width: 1380, height: 580, position: 'absolute', right: 0, left: 0
}
const headStyle = {
  width: '100%',
  height: 70,
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center'
}
const textStyle = {
  flex: 1, 
  //paddingLeft: 5, 
  paddingTop: 5, 
  textAlign: 'center' ,
  fontSize: 30
}
const items = [
  {
    id: 1,
    value: 'Company 1'
  },
  {
    id: 2,
    value: 'Company 2'
  },
  {
    id: 3,
    value: 'Company 3'
  },
]
class Dashboard extends Component {

  constructor(props) {
    super(props)
  
    this.state = {
       open: false,
       search: false,
    };
  };
  

  handleSubmit = () => {
    this.props.history.push('/storelist');
  }

  handleOpen = () => {
    this.setState({open: true})
  }

  handleClose = () => {
    this.setState({open: false})
  }

  handleSearchOpen = () => {
    this.setState({search: true})
  }

  handleSearchClose = () => {
    this.setState({search: false})
  }

  render() {
    return (
      <div className='custom'>
        <AppBar visitor={10} />
        <img src={background} alt='background' style={imageStyle} />

          <div className='exhibitionbtn' onClick={this.handleSearchOpen}>

          </div> 
          <div className='chat-minimised' onClick={this.handleOpen}>
    
        </div>
        <Modal
        open={this.state.open}
        onClose={this.handleClose}
        // aria-labelledby="simple-modal-title"
        // aria-describedby="simple-modal-description"
      >
        <div className='chat-container'>
        
        <ChatBox />


        </div>
      </Modal>
      <Modal
      open={this.state.search}
      onClose={this.handleSearchClose}
      >
        <div className='search-stalls'>
          <ExhibitionDirectory />
        <div style={headStyle}>
            <h3 style={textStyle}>Exhibition Directory</h3>
          </div>
        </div>


      </Modal>
          {/* </div> */}
          
          {/* <div className='row'>
            <div className="col s12 m6">
              <ProjectList />
            </div>
            <div className="col s12 m5 offset-m1">
              <Notification />
            </div>
          </div> */}
          <BottomBar />
          
        
       </div>

    );
  }
}





export default Dashboard;
