import React, { Component } from 'react';
import Background from '../../assets/images/stall1.jpg'
//import Modal from '../modelbox/ModelBox'
import TileBlock from '../tileblock/TileBlock'
import AppBar from '../AppBar'
import './stallDisplay.css'
import Modal from '@material-ui/core/Modal';
import CustomListItem from './CustomList'
import CloseIcon from '@material-ui/icons/Close';
import ArrowDropDownIcon from '@material-ui/icons/ArrowDropDown';
import { Dropdown } from 'semantic-ui-react'

const optionType = [
    { key: "all", text: "ALL", value: "all" },
    { key: "trending", text: "TRENDING", value: "trending" },
    { key: "new", text: "NEW", value: "new" }
]

const imageStyle = {
    width: 2047, height: 890, position: 'absolute', right: 0, left: 0
}
const headStyle = {
    width: '100%',
    height: 50,
    display: 'flex'
}
const headStyle2 = {
    backgroundColor: 'gray',
    width: '99%',
    marginLeft: 5,
    height: 60,
    display: 'flex',
    marginBottom: 10,
    zIndex: 2,
    position: 'relative',
}
const dropdownStyle = {
    display: 'flex',
    flexDirection: 'column',
    marginTop: 40,
}



class StallDisplay extends Component {
    state = {
        show: false,
        open: false,
        setOpen: false
    };

    showModal = () => {
        this.setState({ setOpen: true, open: true });
        console.log(this.state);
    };

    hideModal = () => {
        this.setState({ setOpen: false, open: false });
    };
    _onSelect = () => {
        console.log('_onSelect');
    }
    changeMethod = (event, data) => {
        this.setState({
            filterValue: data.value
        })
        console.log(data)
    }
    render() {
        return (

            <div className={'custom'} >
                <AppBar />
                <img src={Background} alt='background' style={imageStyle} />
                <div className='aboutbutton' onClick={this.showModal}>

                </div>
               
                <Modal
                    open={this.state.open}
                    onClose={this.hideModal}
                    aria-labelledby="simple-modal-title"
                    aria-describedby="simple-modal-description"
                >
                    <div className='modal-div'>

                        <div style={headStyle}>
                            <h6 style={{ flex: 1, paddingLeft: 15, paddingTop: 5,  }}>Product Details</h6>
                            <CloseIcon style={{ marginRight: 10, marginTop: 10, fontSize: 20 }} />
                        </div>
                        <div style={headStyle2}>
                            <p style={{ paddingLeft: 20, fontSize: 18, marginRight: 20, marginTop: 10 }}>Filter by</p>
                            <Dropdown
                                placeholder='All'
                                defaultValue="ALL"
                                //text='Filter'
                                icon='filter'
                                floating
                                labeled
                                button
                                className='icon'
                                selection
                                name="filterBy"
                                options={optionType}
                                onChange={this.changeMethod}
                                style={{height: 45, marginTop: 10}}
                            />
                            {/* <Dropdown arrowOpen={<ArrowDropDownIcon />} options={options} className='dropDown' onChange={() => console.log('onSekec')}  placeholder="Select an option" menuClassName='myMenuClassName' /> */}
                        </div>
                        <CustomListItem />





                    </div>
                </Modal>

            </div>

        );
    }
}

export default StallDisplay;
