import React, { Component } from 'react';
// import Notification from './Notification'
// import ProjectList from '../../project/ProjectList';
import background from '../../assets/images/lobbey.jpg'
import AppBar from '../AppBar'
import BottomBar from '../bottombar/BottomBar'
import Modal from '@material-ui/core/Modal';
import './dashboard.css';
// import CustomListItem from '../ListItem'
import ChatBox from '../chatbox/ChatBox'



const imageStyle = {
  width: 1380, height: 580, position: 'absolute', right: 0, left: 0
}


class Dashboard extends Component {

  constructor(props) {
    super(props)
  
    this.state = {
       open: false,
       search: false,
    };
  };
  

  handleSubmit = () => {
    this.props.history.push('/storelist');
  }

  handleOpen = () => {
    this.setState({open: true})
  }

  handleClose = () => {
    this.setState({open: false})
  }

  handleSearchOpen = () => {
    this.setState({search: true})
  }

  handleSearchClose = () => {
    this.setState({search: false})
  }

  render() {
    return (
      <div className='custom'>
        <AppBar visitor={10} />
        <img src={background} alt='background' style={imageStyle} />

          <div className='exhibitionbtn' onClick={this.handleSearchOpen}>

          </div> 
          <div className='chat-minimised' onClick={this.handleOpen}>
    
        </div>
        <Modal
        open={this.state.open}
        onClose={this.handleClose}
        // aria-labelledby="simple-modal-title"
        // aria-describedby="simple-modal-description"
      >
        <div className='chat-container'>
        
        <ChatBox />


        </div>
      </Modal>
      <Modal
      open={this.state.search}
      onClose={this.handleSearchClose}
      >
        <div className='search-container'>
        <h2 id="modal-title">Exhibition Directory</h2>
        </div>


      </Modal>
          {/* </div> */}
          
          {/* <div className='row'>
            <div className="col s12 m6">
              <ProjectList />
            </div>
            <div className="col s12 m5 offset-m1">
              <Notification />
            </div>
          </div> */}
          <BottomBar />
          
        
       </div>

    );
  }
}





export default Dashboard;
