import React, { Component } from 'react';
import Background from '../../assets/images/stall1.jpg'
import Modal from '../modelbox/ModelBox'
import TileBlock from '../tileblock/TileBlock'
import './stallDisplay.css'

class StallDisplay extends Component {
    state = { show: false };

    showModal = () => {
        this.setState({ show: true });
        console.log(this.state);
    };

    hideModal = () => {
        this.setState({ show: false });
    };
    render() {
        return (

            <div className={'custom'} >
                <div className='backgroundImage'>
                    <div className='stallbutton' onClick={this.showModal}>
                        {/* <p >Stalls</p> */}
                    </div>
                </div>
                <Modal show={this.state.show} handleClose={this.hideModal}>
                    <TileBlock  title='About'/>
                    <br />
                    <TileBlock title='About'/>


                    <TileBlock title='About'/>
                </Modal>

            </div>


        );
    }
}

export default StallDisplay;

const container = {
    backgroundImage: `url(${Background})`,
    height: '100vh',
    backgroundPosition: 'center',
    backgroundRepeat: 'no - repeat',
    backgroundSize: 'fit'
}

const stallPosition = {
    position: 'absolute',
    backgroundColor: 'red',
    height: 85,
    width: 130,
    //transform,
    margin: 0,
    left: '41%',
    top: '58%'
    //width: '50%'
}

const container2 = {
    position: 'absolute',
    top: '60%',
    left: '40%',
    transform: 'translate(' + -60 + '+%, ' + -30 + '%)',
    width: 400,
    height: 500,
    backgroundColor: 'blue'
}
