import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import BottomNavigation from '@material-ui/core/BottomNavigation';
import BottomNavigationAction from '@material-ui/core/BottomNavigationAction';
import RestoreIcon from '@material-ui/icons/Restore';
import FavoriteIcon from '@material-ui/icons/Favorite';
import LocationOnIcon from '@material-ui/icons/LocationOn';
import Modal from '@material-ui/core/Modal';
import InfoSearchBox from './InfoSearchBox'

const useStyles = makeStyles({
  root: {
    width: 1000,
    height: 80,
    position: 'absolute',
    bottom: 50,
    left: 800,
    backgroundColor: 'transparent'
  },
  paper: {
    position: 'absolute',
    width: 400,
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
  },
});



export default function SimpleBottomNavigation() {
  const classes = useStyles();
  const [value, setValue] = React.useState(0);
  const [open, setOpen] = React.useState(false);

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const body = (
    <div className={classes.paper}>
      <h2 id="simple-modal-title">Text in a modal</h2>
      <p id="simple-modal-description">
        Duis mollis, est non commodo luctus, nisi erat porttitor ligula.
      </p>
    </div>
  );

  return (
    <div>
    <BottomNavigation
      value={value}
      onChange={(event, newValue) => {
        setValue(newValue);
      }}
      showLabels
      className={classes.root}
    >
      <BottomNavigationAction onClick={() => console.log('I am searched clicked')} label="Recents" icon={<RestoreIcon />} style={{height: 70, width: 60, backgroundColor: 'black', color: 'white'}}/>
      <BottomNavigationAction label="Favorites" icon={<FavoriteIcon />} style={{height: 70, width: 60, backgroundColor: 'black', color: 'white'}} />
      <BottomNavigationAction label="Nearby" icon={<LocationOnIcon />} style={{height: 70, width: 60, backgroundColor: 'black', color: 'white'} }/>
    </BottomNavigation>
    <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="simple-modal-title"
        aria-describedby="simple-modal-description"
      >
        {body}
      </Modal>
    </div>
  );
}
